&lt;h3 id=&#34;4&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#4&#34;&gt;#&lt;/a&gt; 4&lt;/h3&gt;
&lt;p&gt;&lt;strong&gt;原代码：&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;&lt;figure class=&#34;highlight java&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;nv = rhs.getNameValue();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;assertNotNull(nv); &lt;span class=&#34;comment&#34;&gt;// subType = sdp&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;assertNull(rhs.getGroupedExpression());&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;assertEquals(&lt;span class=&#34;string&#34;&gt;&amp;quot;subType&amp;quot;&lt;/span&gt;, nv.getName().getName());&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;assertEquals(&lt;span class=&#34;string&#34;&gt;&amp;quot;=&amp;quot;&lt;/span&gt;, nv.getOp().getOperator());&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;assertEquals(&lt;span class=&#34;string&#34;&gt;&amp;quot;\&amp;quot;sdp\&amp;quot;&amp;quot;&lt;/span&gt;, nv.getValue().getValue());&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;assertEquals(&lt;span class=&#34;string&#34;&gt;&amp;quot;AND&amp;quot;&lt;/span&gt;, rhs.getOperator().getOperator());&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;/p&gt;
