&lt;h4 id=&#34;assertfalserhsisbinaryexpr&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#assertfalserhsisbinaryexpr&#34;&gt;#&lt;/a&gt; assertFalse(rhs.isBinaryExpr());&lt;/h4&gt;
