=args.l2_emb,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                                                 scope=&lt;span class=&#34;string&#34;&gt;&amp;quot;input_embeddings&amp;quot;&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                                                 with_t=&lt;span class=&#34;literal&#34;&gt;True&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                                                 reuse=reuse&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                                                 )&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &lt;span class=&#34;comment&#34;&gt;# Positional Encoding&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            t, pos_emb_table = embedding(&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                tf.tile(tf.expand_dims(tf.&lt;span class=&#34;built_in&#34;&gt;range&lt;/span&gt;(tf.shape(self.input_seq)[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;]), &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;), [tf.shape(self.input_seq)[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;], &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;]),&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                vocab_size=args.maxlen,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                num_units=args.hidden_units,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                zero_pad=&lt;span class=&#34;literal&#34;&gt;False&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                scale=&lt;span class=&#34;literal&#34;&gt;False&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                l2_reg=args.l2_emb,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                scope=&lt;span class=&#34;string&#34;&gt;&amp;quot;dec_pos&amp;quot;&lt