34;keyword&#34;&gt;from&lt;/span&gt; transformers &lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; BertTokenizer, BertForMaskedLM&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;from&lt;/span&gt; torch.utils.data &lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; DataLoader, Dataset&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# 自定义数据集&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;class&lt;/span&gt; &lt;span class=&#34;title class_&#34;&gt;ClozeDataset&lt;/span&gt;(&lt;span class=&#34;title class_ inherited__&#34;&gt;Dataset&lt;/span&gt;):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;def&lt;/span&gt; &lt;span class=&#34;title function_&#34;&gt;__init__&lt;/span&gt;(&lt;span class=&#34;params&#34;&gt;self, texts, tokenizer, max_length=&lt;span class=&#34;number&#34;&gt;512&lt;/span&gt;&lt;/span&gt;):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        self.texts = texts&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        self.tokenizer = tokenizer&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        self.max_length = max_length&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;def&lt;/span&gt; &lt;span class=&#34;title function_&#34;&gt;__len__&lt;/span&gt;(&lt;span class=&#34;params&#34;&gt;self&lt;/span&gt;):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; &lt;span class=&#34;built_in&#34;&gt;len&lt;/span&gt;(self.texts)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;def&lt;/span&gt; &lt;span class=&#34;title function_&#34;&gt;__getitem__&lt;/span&gt;(&lt;span class=&#34;params&#34;&gt;self, idx&lt;/span&gt;):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        text = self.texts[idx]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        encoding = self.tokenizer(text, return_tensors=&lt;span class=&#34;string&#34;&gt;&amp;#x27;pt&amp;#x27;&lt;/span&gt;, max_length=self.max_length, truncation=&lt;span class=&#34;literal&#34;&gt;True&lt;/span&gt;, padding=&lt;span class=&#34;string&#34;&gt;&amp;#x27;max_length&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        input_ids = encoding[&lt;span class=&#34;string&#34;&gt;&amp;#x27;input_ids&amp;#x27;&lt;/span&gt;].squeeze()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        attention_mask = encoding[&lt;span class=&#34;string&#34;&gt;&amp;#x27;attention_mask&amp;#x27;&lt;/span&gt;].squeeze()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;comment&#34;&gt;# 随机掩盖一些词&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        labels = input_ids.clone()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        rand = torch.rand(input_ids.shape)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        mask_arr = (rand &amp;lt; &lt;span class=&#34;number&#34;&gt;0.15&lt;/span&gt;) * (input_ids != &lt;span class=&#34;number&#34;&gt;101&lt;/span&gt;) * (input_ids != &lt;span class=&#34;number&#34;&gt;102&lt;/span&gt;) * (input_ids != &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;)  &lt;span class=&#34;comment&#34;&gt;# 不掩盖特殊标记&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        selection = torch.flatten(mask_arr.nonzero()).tolist()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        input_ids[selection] = &lt;span class=&#34;number&#34;&gt;103&lt;/span&gt;  &lt;span class=&#34;comment&#34;&gt;# 掩盖的词替换为 [MASK] 标记&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &lt;span class=&#34;string&#34;&gt;&amp;#x27;input_ids&amp;#x27;&lt;/span&gt;: input_ids,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &lt;span class=&#34;string&#34;&gt;&amp;#x27;attention_mask&amp;#x27;&lt;/span&gt;: attention_mask,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &lt;span class=&#34;string&#34;&gt;&amp;#x27;labels&amp;#x27;&lt;/span&gt;: labels&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# 准备数据&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;texts = [&lt;span class=&#34;string&#34;&gt;&amp;quot;Hello, how are you?&amp;quot;&lt;/span&gt;, &lt;span class=&#34;string&#34;&gt;&amp;quot;I am fine, thank you.&amp;quot;&lt;/span&gt;, &lt;span class=&#34;string&#34;&gt;&amp;quot;What is your name?&amp;quot;&lt;/span&gt;]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;tokenizer = BertTokenizer.from_pretrained(&lt;span class=&#34;string&#34;&gt;&amp;#x27;bert-base-uncased&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;dataset = ClozeDataset(texts, tokenizer)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;dataloader = DataLoader(dataset, batch_size=&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;, shuffle=&lt;span class=&#34;literal&#34;&gt;True&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# 加载模型&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;model = BertForMaskedLM.from_pretrained(&lt;span class=&#34;string&#34;&gt;&amp;#x27;bert-base-uncased&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;model.train()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# 定义优化器&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;optimizer = torch.optim.AdamW(model.parameters(), lr=&lt;span class=&#34;number&#34;&gt;1e-5&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# 训练模型&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;epochs = &lt;span class=&#34;number&#34;&gt;3&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; epoch &lt;span class=&#34;keyword&#34;&gt;in&lt;/span&gt; &lt;span class=&#34;built_in&#34;&gt;range&lt;/span&gt;(epochs):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; batch &lt;span class=&#34;keyword&#34;&gt;in&lt;/span&gt; dataloader:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        optimizer.zero_grad()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        input_ids = batch[&lt;span class=&#34;string&#34;&gt;&amp;#x27;input_ids&amp;#x27;&lt;/span&gt;]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        attention_mask = batch[&lt;span class=&#34;string&#34;&gt;&amp;#x27;attention_mask&amp;#x27;&lt;/span&gt;]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        labels = batch[&lt;span class=&#34;string&#34;&gt;&amp;#x27;labels&amp;#x27;&lt;/span&gt;]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        outputs = model(input_ids=input_ids, attention_mask=attention_mask, labels=labels)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        loss = outputs.loss&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        loss.backward()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        optimizer.step()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;f&amp;quot;Epoch: &lt;span class=&#34;subst&#34;&gt;&amp;#123;epoch + &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;&amp;#125;&lt;/span&gt;, Loss: &lt;span class=&#34;subst&#34;&gt;&amp;#123;loss.item()&amp;#125;&lt;/span&gt;&amp;quot;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;quot;Training complete&amp;quot;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;/p&gt;
