an&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;end&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;else&lt;/span&gt; &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt;(RBI_n==&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;&amp;amp;&amp;amp;D==&lt;span class=&#34;number&#34;&gt;4&amp;#x27;b0000&lt;/span&gt;) &lt;span class=&#34;keyword&#34;&gt;begin&lt;/span&gt;     &lt;span class=&#34;comment&#34;&gt;//灭零输入&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        L=&lt;span class=&#34;number&#34;&gt;8&amp;#x27;b00000000&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        RBO_n=&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;;                            &lt;span class=&#34;comment&#34;&gt;//灭零状态&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;end&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
